package com.mkpits.arrays;

public class MultidimensionalArrayBasics {
        // Multidimensional Array - the array which contain number of rows and coloums 
	public static void main(String[] args) {
		// TODO Auto-generated method stub
          String [][]name = {{"Msr.","Ms.","Mr."},{"Karan","Akshu"}};
          System.out.println(name[0][0] + " " +name[1][0]);
          System.out.println(name[0][1] + " " +name[1][1]);
          System.out.println(name[0][2] + " " +name[1][0]);

	}

}
