package com.mkpits.datatype;

public class Floating {
        // Floating - float,double - used for floating/decimal values
	public static void main(String[] args) {
		// TODO Auto-generated method stub
        float floatNumb = 5.42f;
        // memory - 4byte
        // appends with f/F
        // default value - 0.0
        System.out.println(floatNumb);
        double doubleNumb = 454848.555;
        // memory - 8byte
        // default value 0.0
        System.out.println(doubleNumb);
	}

}
